INSERT INTO `explorer_query` VALUES (2,'Every SN Ia with >5 epochs of ZTF gri photometry','SELECT *\r\nFROM YSE_App_transient\r\nWHERE id IN\r\n    (SELECT DISTINCT t.id\r\n     FROM YSE_App_transient t\r\n     INNER JOIN YSE_App_transientphotometry tp ON t.id = tp.transient_id\r\n     INNER JOIN YSE_App_transientphotdata tpd ON tp.id = tpd.photometry_id\r\n     INNER JOIN YSE_App_instrument i ON i.id = tp.instrument_id\r\n     WHERE i.name = \"Direct/4Kx4K\" #Swope\r\n     GROUP BY t.id\r\n     HAVING count(t.id) >= 50)\r\n  AND best_spec_class_id = 5; #SN Ia','Should correspond to (roughly) the Foundation cosmology sample.','2018-06-21 17:41:01.141530','2022-01-05 00:28:01.088393',2,0,''),(3,'Every SN Ia within 40 kpc of a galaxy at z ~ 0.03','SELECT t.name,\r\n       t.ra AS transient_RA,\r\n       t.`dec` AS transient_Dec,\r\n       t.TNS_spec_class AS spec_class,\r\n       t.redshift AS transient_z,\r\n       h.ra AS host_RA,\r\n       h.`dec` AS host_Dec,\r\n       h.redshift AS host_z,\r\n       DEGREES(ACOS(SIN(RADIANS(t.`dec`))*SIN(RADIANS(h.`dec`)) + COS(RADIANS(t.`dec`))*COS(RADIANS(h.`dec`))*COS(RADIANS(ABS(t.ra - h.ra)))))*3600 AS AngSepArcSec,\r\n       (3e+5*COALESCE(t.redshift, h.redshift)/73) AS LuminosityDistanceMpc,\r\n       (3e+5*COALESCE(t.redshift, h.redshift)/73)/POW((1.0 + COALESCE(t.redshift, h.redshift)), 2) AS AngularDiameterDistanceMpc,\r\n       (ACOS(SIN(RADIANS(t.`dec`))*SIN(RADIANS(h.`dec`)) + COS(RADIANS(t.`dec`))*COS(RADIANS(h.`dec`))*COS(RADIANS(ABS(t.ra - h.ra))))*(3e+5*COALESCE(t.redshift, h.redshift)/73)/POW((1.0 + COALESCE(t.redshift, h.redshift)), 2)*1000) AS ProjectedDistKpc\r\nFROM YSE_App_transient t\r\nINNER JOIN YSE_App_host h ON h.id = t.host_id\r\nWHERE t.host_id IS NOT NULL\r\n  AND (t.redshift\r\n       OR h.redshift) IS NOT NULL\r\n  AND COALESCE(t.redshift, h.redshift) > 0.028\r\n  AND COALESCE(t.redshift, h.redshift) < 0.032\r\n  AND t.TNS_spec_class = \"SN Ia\"\r\n  AND (ACOS(SIN(RADIANS(t.`dec`))*SIN(RADIANS(h.`dec`)) + COS(RADIANS(t.`dec`))*COS(RADIANS(h.`dec`))*COS(RADIANS(ABS(t.ra - h.ra))))*(3e+5*COALESCE(t.redshift, h.redshift)/73)/POW((1.0 + COALESCE(t.redshift, h.redshift)), 2)*1000) < 40;','This should be all potential Cepheid/TRGB SNe.','2018-06-21 22:08:29.300123','2022-01-05 00:30:42.008950',2,0,''),(4,'Every SN Ia within 80 kpc of a galaxy at z > 0.013 and z < 0.017','SELECT t.name,\r\n       t.ra AS transient_RA,\r\n       t.`dec` AS transient_Dec,\r\n       t.disc_date AS disc_date,\r\n       t.TNS_spec_class AS spec_class,\r\n       t.redshift AS transient_z,\r\n       h.ra AS host_RA,\r\n       h.`dec` AS host_Dec,\r\n       h.redshift AS host_z\r\n/*       DEGREES(ACOS(SIN(RADIANS(t.`dec`))*SIN(RADIANS(h.`dec`)) + COS(RADIANS(t.`dec`))*COS(RADIANS(h.`dec`))*COS(RADIANS(ABS(t.ra - h.ra)))))*3600 AS AngSepArcSec,\r\n       (3e+5*COALESCE(t.redshift, h.redshift)/73) AS LuminosityDistanceMpc,\r\n       (3e+5*COALESCE(t.redshift, h.redshift)/73)/POW((1.0 + COALESCE(t.redshift, h.redshift)), 2) AS AngularDiameterDistanceMpc,\r\n       (ACOS(SIN(RADIANS(t.`dec`))*SIN(RADIANS(h.`dec`)) + COS(RADIANS(t.`dec`))*COS(RADIANS(h.`dec`))*COS(RADIANS(ABS(t.ra - h.ra))))*(3e+5*COALESCE(t.redshift, h.redshift)/73)/POW((1.0 + COALESCE(t.redshift, h.redshift)), 2)*1000) AS ProjectedDistKpc\r\n*/\r\nFROM YSE_App_transient t\r\nINNER JOIN YSE_App_host h ON h.id = t.host_id\r\nWHERE (t.redshift\r\n       OR h.redshift) IS NOT NULL\r\n  AND COALESCE(t.redshift, h.redshift) > 0\r\n  AND COALESCE(t.redshift, h.redshift) >= 0.013\r\n  AND COALESCE(t.redshift, h.redshift) <= 0.017\r\n  AND t.TNS_spec_class = \"SN Ia\"\r\n  /* AND t.status_id != 3 */\r\n  /*AND (ACOS(SIN(RADIANS(t.`dec`))*SIN(RADIANS(h.`dec`)) + COS(RADIANS(t.`dec`))*COS(RADIANS(h.`dec`))*COS(RADIANS(ABS(t.ra - h.ra))))*(3e+5*COALESCE(t.redshift, h.redshift)/73)/POW((1.0 + COALESCE(t.redshift, h.redshift)), 2)*1000) < 80;*/','','2018-08-09 17:46:06.913136','2021-04-16 15:49:55.420255',3,0,''),(5,'Unclassified Bright Transients (South)','SELECT DISTINCT t.name,\r\n       t.id,\r\n       t.ra,\r\n       t.dec,\r\n       t.disc_date,\r\n       t.status_id,\r\n       pb.name AS `filter`,\r\n       g1.min_mag AS \'Recent mag\',\r\n       pd.obs_date\r\nFROM YSE_App_transientphotdata pd\r\nINNER JOIN YSE_App_transientphotometry tp ON pd.photometry_id = tp.id\r\nINNER JOIN YSE_App_transient t ON tp.transient_id = t.id\r\nINNER JOIN YSE_App_photometricband pb ON pb.id = pd.band_id\r\nINNER JOIN\r\n  (SELECT a3.id AS transient_id,\r\n          MIN(a1.mag) AS min_mag\r\n   FROM YSE_App_transientphotdata a1\r\n   INNER JOIN YSE_App_transientphotometry a2 ON a1.photometry_id = a2.id\r\n   INNER JOIN YSE_App_transient a3 ON a2.transient_id = a3.id\r\n   WHERE mag IS NOT NULL\r\n   GROUP BY a3.id) AS g1 ON g1.min_mag = pd.mag\r\nAND g1.transient_id = t.id\r\nWHERE (t.best_spec_class_id is NULL OR t.best_spec_class_id = 1)\r\n  AND t.disc_date >= curdate() - INTERVAL DAYOFWEEK(curdate())+90 DAY\r\n  AND g1.min_mag < 18\r\n  AND pd.obs_date >= curdate() - INTERVAL DAYOFWEEK(curdate())+14 DAY\r\n  AND pd.obs_date < curdate() - INTERVAL DAYOFWEEK(curdate())-1 DAY\r\n  AND t.dec <= 30\r\nORDER BY t.name DESC,\r\n         pd.obs_date ASC;','Find all of the supernovae that are unclassified, bright, and in the South.  This requires some information about the brightness and the time of the last data point.','2018-09-14 11:31:49.060948','2022-03-24 17:30:43.502564',11,0,''),(6,'Every SN Ia tagged as HST','SELECT *\r\nFROM YSE_App_transient t\r\nWHERE t.redshift IS NOT NULL\r\n  AND t.TNS_spec_class = \"SN Ia\"\r\n##  AND t.tags = \'HST\';','','2018-10-08 03:20:40.286269','2022-03-18 00:25:02.317163',3,0,'explorer'),(9,'Unclassified Bright Transients (North)','SELECT DISTINCT t.name,\r\n       t.id,\r\n       t.ra,\r\n       t.dec,\r\n       t.disc_date,\r\n       t.status_id,\r\n       pb.name AS `filter`,\r\n       g1.min_mag AS \'Recent mag\',\r\n       pd.obs_date\r\nFROM YSE_App_transientphotdata pd\r\nINNER JOIN YSE_App_transientphotometry tp ON pd.photometry_id = tp.id\r\nINNER JOIN YSE_App_transient t ON tp.transient_id = t.id\r\nINNER JOIN YSE_App_photometricband pb ON pb.id = pd.band_id\r\nINNER JOIN\r\n  (SELECT a3.id AS transient_id,\r\n          MIN(a1.mag) AS min_mag\r\n   FROM YSE_App_transientphotdata a1\r\n   INNER JOIN YSE_App_transientphotometry a2 ON a1.photometry_id = a2.id\r\n   INNER JOIN YSE_App_transient a3 ON a2.transient_id = a3.id\r\n   WHERE mag IS NOT NULL\r\n   GROUP BY a3.id) AS g1 ON g1.min_mag = pd.mag\r\nAND g1.transient_id = t.id\r\nWHERE t.best_spec_class_id is NULL\r\n  AND g1.min_mag < 18\r\n  AND t.dec >= -30\r\nORDER BY t.name DESC,\r\n         pd.obs_date ASC;','Find all of the supernovae that are unclassified, bright, and in the North.  This requires some information about the brightness and the time of the last data point.','2019-01-24 05:38:52.517613','2022-01-09 14:47:50.731083',9,0,''),(11,'Interesting New Targets','SELECT DISTINCT t.name,\r\n       t.id,\r\n       t.ra,\r\n       t.dec,\r\n       t.disc_date,\r\n       t.status_id,\r\n       pb.name AS `filter`,\r\n       g1.min_mag AS \'Recent mag\',\r\n       pd.obs_date\r\nFROM YSE_App_transientphotdata pd\r\nINNER JOIN YSE_App_transientphotometry tp ON pd.photometry_id = tp.id\r\nINNER JOIN YSE_App_transient t ON tp.transient_id = t.id\r\nINNER JOIN YSE_App_photometricband pb ON pb.id = pd.band_id\r\nINNER JOIN YSE_App_host h ON h.id = t.host_id\r\nINNER JOIN\r\n  (SELECT a3.id AS transient_id,\r\n          MIN(a1.mag) AS min_mag\r\n   FROM YSE_App_transientphotdata a1\r\n   INNER JOIN YSE_App_transientphotometry a2 ON a1.photometry_id = a2.id\r\n   INNER JOIN YSE_App_transient a3 ON a2.transient_id = a3.id\r\n   WHERE mag IS NOT NULL\r\n   GROUP BY a3.id) AS g1 ON g1.min_mag = pd.mag\r\nAND g1.transient_id = t.id\r\nWHERE t.status_id = 1\r\n  AND t.mw_ebv < 0.5\r\n  AND (g1.min_mag < 17\r\n  OR  ( COALESCE(t.redshift, h.redshift) > 0\r\n  AND COALESCE(t.redshift, h.redshift) <= 0.01\r\n  AND (ACOS(SIN(RADIANS(t.`dec`))*SIN(RADIANS(h.`dec`)) + COS(RADIANS(t.`dec`))*COS(RADIANS(h.`dec`))*COS(RADIANS(ABS(t.ra - h.ra))))*(3e+5*COALESCE(t.redshift, h.redshift)/73)/POW((1.0 + COALESCE(t.redshift, h.redshift)), 2)*1000) < 40 ) )\r\nORDER BY t.name DESC,\r\n         pd.obs_date ASC;','Just the best candidates for fast triaging','2019-01-25 05:47:05.599558','2022-03-21 16:47:34.667703',9,0,''),(12,'SNe with z < 0.01','SELECT t.name,\r\n       t.ra AS transient_RA,\r\n       t.`dec` AS transient_Dec,\r\n       t.disc_date AS disc_date,\r\n       t.TNS_spec_class AS spec_class,\r\n       t.redshift AS transient_z,\r\n       h.ra AS host_RA,\r\n       h.`dec` AS host_Dec,\r\n       h.redshift AS host_z,\r\n       DEGREES(ACOS(SIN(RADIANS(t.`dec`))*SIN(RADIANS(h.`dec`)) + COS(RADIANS(t.`dec`))*COS(RADIANS(h.`dec`))*COS(RADIANS(ABS(t.ra - h.ra)))))*3600 AS AngSepArcSec,\r\n       (3e+5*COALESCE(t.redshift, h.redshift)/73) AS LuminosityDistanceMpc,\r\n       (3e+5*COALESCE(t.redshift, h.redshift)/73)/POW((1.0 + COALESCE(t.redshift, h.redshift)), 2) AS AngularDiameterDistanceMpc,\r\n       (ACOS(SIN(RADIANS(t.`dec`))*SIN(RADIANS(h.`dec`)) + COS(RADIANS(t.`dec`))*COS(RADIANS(h.`dec`))*COS(RADIANS(ABS(t.ra - h.ra))))*(3e+5*COALESCE(t.redshift, h.redshift)/73)/POW((1.0 + COALESCE(t.redshift, h.redshift)), 2)*1000) AS ProjectedDistKpc\r\nFROM YSE_App_transient t\r\nINNER JOIN YSE_App_host h ON h.id = t.host_id\r\nWHERE t.host_id IS NOT NULL\r\n  AND (t.redshift\r\n       OR h.redshift) IS NOT NULL\r\n  AND COALESCE(t.redshift, h.redshift) > 0\r\n  AND COALESCE(t.redshift, h.redshift) <= 0.01\r\n  AND (t.TNS_spec_class LIKE \'SN II\' or t.TNS_spec_class LIKE \'SN Ib\' or \r\n      t.TNS_spec_class LIKE \'SN Ic\' or t.TNS_spec_class LIKE \'SN IIb\' or\r\n      t.TNS_spec_class LIKE \'LBV\' or t.TNS_spec_class LIKE \'SN IIn\')\r\n  AND (ACOS(SIN(RADIANS(t.`dec`))*SIN(RADIANS(h.`dec`)) + COS(RADIANS(t.`dec`))*COS(RADIANS(h.`dec`))*COS(RADIANS(ABS(t.ra - h.ra))))*(3e+5*COALESCE(t.redshift, h.redshift)/73)/POW((1.0 + COALESCE(t.redshift, h.redshift)), 2)*1000) < 40;','','2019-02-05 02:52:08.369699','2022-03-22 20:26:28.158980',8,0,''),(13,'LCOGT Targets','SELECT DISTINCT t.name,\r\n                t.id,\r\n                t.ra,\r\n                t.dec,\r\n                t.disc_date,\r\n                t.status_id,\r\n                pb.name AS `filter`,\r\n                g1.min_mag,\r\n       			g1.min_date,\r\n                pd.obs_date\r\nFROM YSE_App_transientphotdata pd\r\nINNER JOIN YSE_App_transientphotometry tp ON pd.photometry_id = tp.id\r\nINNER JOIN YSE_App_transient t ON tp.transient_id = t.id\r\nINNER JOIN YSE_App_photometricband pb ON pb.id = pd.band_id\r\nINNER JOIN YSE_App_transientphotdata tpd ON tp.id = tpd.photometry_id\r\nINNER JOIN YSE_App_transient_tags tt ON tt.transient_id = t.id\r\nINNER JOIN YSE_App_transienttag tg ON tg.id = tt.transienttag_id\r\nINNER JOIN\r\n   (SELECT a3.id AS transient_id,\r\n    MIN(a1.mag) AS min_mag,\r\n    MIN(obs_date) AS min_date,\r\n    obs_date\r\n   FROM YSE_App_transientphotdata a1\r\n   INNER JOIN YSE_App_transientphotometry a2 ON a1.photometry_id = a2.id\r\n   INNER JOIN YSE_App_transient a3 ON a2.transient_id = a3.id\r\n   WHERE mag IS NOT NULL\r\n   GROUP BY a3.id, a1.obs_date) AS g1 ON g1.min_mag = pd.mag\r\nAND g1.transient_id = t.id\r\nWHERE tg.name = \'LCOGT\';\r\n\r\n','','2019-03-16 13:17:54.744713','2021-11-01 01:35:11.966995',35,0,'explorer'),(14,'Late-time SN Ia','SELECT t.name,\r\n       t.ra AS transient_RA,\r\n       t.`dec` AS transient_Dec,\r\n       g1.min_mag,\r\n       g1.min_date,\r\n       t.disc_date AS disc_date,\r\n       t.TNS_spec_class AS spec_class,\r\n       t.redshift AS transient_z,\r\n       t.mw_ebv AS mw_ebv,\r\n       h.ra AS host_RA,\r\n       h.`dec` AS host_Dec,\r\n       h.redshift AS host_z,\r\n       pd.discovery_point,\r\n       DEGREES(ACOS(SIN(RADIANS(t.`dec`))*SIN(RADIANS(h.`dec`)) + COS(RADIANS(t.`dec`))*COS(RADIANS(h.`dec`))*COS(RADIANS(ABS(t.ra - h.ra)))))*3600 AS AngSepArcSec,\r\n       (3e+5*COALESCE(t.redshift, h.redshift)/73) AS LuminosityDistanceMpc,\r\n       (3e+5*COALESCE(t.redshift, h.redshift)/73)/POW((1.0 + COALESCE(t.redshift, h.redshift)), 2) AS AngularDiameterDistanceMpc,\r\n       (ACOS(SIN(RADIANS(t.`dec`))*SIN(RADIANS(h.`dec`)) + COS(RADIANS(t.`dec`))*COS(RADIANS(h.`dec`))*COS(RADIANS(ABS(t.ra - h.ra))))*(3e+5*COALESCE(t.redshift, h.redshift)/73)/POW((1.0 + COALESCE(t.redshift, h.redshift)), 2)*1000) AS ProjectedDistKpc\r\nFROM YSE_App_transientphotdata pd\r\nINNER JOIN YSE_App_transientphotometry tp ON pd.photometry_id = tp.id\r\nINNER JOIN YSE_App_transient t ON tp.transient_id = t.id\r\nINNER JOIN YSE_App_photometricband pb ON pb.id = pd.band_id\r\nINNER JOIN YSE_App_host h ON h.id = t.host_id\r\nINNER JOIN\r\n   (SELECT a3.id AS transient_id,\r\n    MIN(a1.mag) AS min_mag,\r\n    MIN(obs_date) AS min_date,\r\n    MAX(obs_date) AS latest_detection,\r\n    obs_date\r\n   FROM YSE_App_transientphotdata a1\r\n   INNER JOIN YSE_App_transientphotometry a2 ON a1.photometry_id = a2.id\r\n   INNER JOIN YSE_App_transient a3 ON a2.transient_id = a3.id\r\n   WHERE mag IS NOT NULL\r\n   GROUP BY a3.id, a1.obs_date) AS g1 ON g1.min_mag = pd.mag\r\nAND g1.transient_id = t.id\r\nWHERE (t.TNS_spec_class = \"SN Ia\" OR t.TNS_spec_class = \"SN Ia-91T-like\" OR t.TNS_spec_class = \"SN Ia-91bg-like\" OR best_spec_class_id = 5)\r\n  AND (t.mw_ebv < 0.5 OR t.mw_ebv IS NULL)\r\n  AND g1.min_mag < 24\r\n  AND g1.min_mag > 5\r\n  AND DATEDIFF(curdate(),disc_date) > 90\r\n  AND DATEDIFF(curdate(),latest_detection) < 14\r\nORDER BY t.name DESC;','','2019-04-02 16:39:15.793320','2022-03-01 18:25:23.418985',9,0,'explorer'),(15,'TESS targets with z < 0.05','SELECT t.name,\r\n       t.ra AS transient_RA,\r\n       t.dec AS transient_Dec,\r\n       t.disc_date AS disc_date,\r\n       t.TNS_spec_class AS spec_class,\r\n       t.redshift AS transient_z,\r\n       g1.min_mag,\r\n       g1.min_date\r\nFROM YSE_App_transientphotdata pd\r\nINNER JOIN YSE_App_transientphotometry tp ON pd.photometry_id = tp.id\r\nINNER JOIN YSE_App_transient t ON tp.transient_id = t.id\r\nINNER JOIN YSE_App_transient_tags tt ON tt.transient_id = t.id\r\nINNER JOIN YSE_App_transienttag tg ON tg.id = tt.transienttag_id\r\nINNER JOIN\r\n  (SELECT a3.id AS transient_id,\r\n          MIN(a1.mag) AS min_mag,\r\n          MIN(obs_date) AS min_date\r\n   FROM YSE_App_transientphotdata a1\r\n   INNER JOIN YSE_App_transientphotometry a2 ON a1.photometry_id = a2.id\r\n   INNER JOIN YSE_App_transient a3 ON a2.transient_id = a3.id\r\n   WHERE mag IS NOT NULL\r\n   GROUP BY a3.id) AS g1 ON g1.min_mag = pd.mag\r\nAND g1.transient_id = t.id\r\nWHERE tg.name = \'TESS\' #AND (DATEDIFF(curdate(), t.disc_date) <= 1000)','','2019-04-05 04:46:37.955751','2021-04-21 22:30:12.375465',9,0,''),(16,'Recent bright SNe with dec<+25','SELECT t.name,\r\n       t.ra AS transient_RA,\r\n       t.`dec` AS transient_Dec,\r\n       t.TNS_spec_class AS spec_class,\r\n       t.redshift AS transient_z,\r\n       h.ra AS host_RA,\r\n       h.`dec` AS host_Dec,\r\n       h.redshift AS host_z\r\nFROM YSE_App_transient t\r\nINNER JOIN YSE_App_host h ON h.id = t.host_id\r\nWHERE t.host_id IS NOT NULL\r\n  AND (t.redshift OR h.redshift) IS NOT NULL\r\n  AND COALESCE(t.redshift, h.redshift) > 0.00003\r\n  AND COALESCE(t.redshift, h.redshift) < 0.005\r\n  AND (t.TNS_spec_class like \'%SN%\')\r\n  AND (t.mw_ebv < 0.5 OR t.mw_ebv IS NULL)\r\n  AND t.dec <= +25\r\nORDER BY t.name DESC;','','2019-04-15 05:59:32.076137','2022-01-19 02:00:56.947267',30,0,'explorer'),(17,'GW Candidates','SELECT t.name, \r\n       t.ra AS transient_RA,\r\n       t.`dec` AS transient_Dec,\r\n       t.disc_date AS disc_date,\r\n       t.TNS_spec_class AS spec_class,\r\n       t.redshift AS transient_z\r\nFROM YSE_App_transient t\r\nINNER JOIN YSE_App_transient_tags tt ON tt.transient_id = t.id\r\nINNER JOIN YSE_App_transienttag tg ON tg.id = tt.transienttag_id\r\nWHERE tg.name = \'GW Candidate\';\r\n','','2019-04-24 16:16:42.419282','2020-01-23 09:53:56.057307',2,0,''),(18,'Playground Query','SELECT t.name, \r\n       t.ra AS transient_RA,\r\n       t.dec AS transient_Dec,\r\n       t.disc_date AS disc_date,\r\n       t.TNS_spec_class AS spec_class,\r\n       t.redshift AS transient_z\r\nFROM YSE_App_transient t\r\nINNER JOIN YSE_App_transient_tags tt ON tt.transient_id = t.id\r\nINNER JOIN YSE_App_transienttag tg ON tg.id = tt.transienttag_id\r\nWHERE t.redshift < 0.042;\r\n	AND t.redshift > 0.023;\r\n	AND DATEDIFF(curdate(), t.disc_date) < 60;\r\n\r\n\r\n\r\n\r\n\r\n\r\n','','2019-04-25 15:47:09.286451','2021-05-19 21:00:49.780554',9,0,''),(19,'test for now','SELECT t.name, \r\n       t.ra AS transient_RA,\r\n       t.dec AS transient_Dec,\r\n       t.TNS_spec_class AS spec_class,\r\n       t.disc_date AS disc_date,\r\n       t.redshift AS transient_z\r\nFROM YSE_App_transient t\r\nWHERE t.redshift BETWEEN 0.023 AND 0.042\r\nAND DATEDIFF(curdate(), t.disc_date) < 60;','','2019-04-25 15:47:29.275016','2019-04-25 16:00:24.007632',9,0,''),(20,'test for now','SELECT t.name, \r\n       t.ra AS transient_RA,\r\n       t.dec AS transient_Dec,\r\n       t.disc_date AS disc_date,\r\n       t.TNS_spec_class AS spec_class,\r\n       t.redshift AS transient_z\r\nFROM YSE_App_transient t\r\nWHERE t.redshift < 0.042;\r\n	AND t.redshift > 0.023;\r\n	AND DATEDIFF(curdate(), t.disc_date) < 60;\r\n\r\n\r\n\r\n\r\n\r\n\r\n','','2019-04-25 15:52:35.763690','2019-04-25 15:52:35.883498',9,0,''),(21,'Test Query','SELECT DISTINCT t.name,\r\n       t.id,\r\n       t.ra,\r\n       t.dec,\r\n       t.disc_date,\r\n       t.status_id,\r\n       pb.name AS `filter`,\r\n       t.TNS_spec_class AS spec_class,\r\n       g1.min_mag AS \'Recent mag\',\r\n       pd.obs_date\r\nFROM YSE_App_transientphotdata pd\r\nINNER JOIN YSE_App_transientphotometry tp ON pd.photometry_id = tp.id\r\nINNER JOIN YSE_App_transient t ON tp.tr